 <script type="text/javascript">dojo.require("dojo.fx");
 dojo.require("dojo.parser");
 dojo.require("dojox.timing._base");
 dojo.addOnLoad(function() {
  var timer = new dojox.timing.Timer(3000);
  timer.start();
  dojo.style("element1", "display", "inline");
  dojo.style("element2", "display", "none");
  dojo.style("element3", "display", "none");
  timer.x = 1;
  timer.onTick = function() {
   dojo.style("element" + this.x, "display", "none");
   this.x++;
   if (this.x > 3)
    this.x = 1;
   dojo.style("element" + this.x, "display", "inline");
  };
 });</script>
